legend( h, 'Average Power vs. Total Power', ['f(x) = p1*x + p2',newline,'       p1 =     429.3',newline,'       p2 =    73390',newline,'R-Squared: 0.8848'], 'Location', 'NorthEast', 'Interpreter', 'none' );
% Label axes
xlabel( 'Average Power (W)', 'Interpreter', 'none' );
ylabel( 'Total Power (J)', 'Interpreter', 'none' );
grid on

